mapper.select:
select count(*)
from tstaff_vote
where sv_viid = #{svViid,jdbcType=INTEGER} and sv_siid = #{svSiid,jdbcType=INTEGER}

select sv_isvoted
from tstaff_vote
where sv_viid = #{svViid,jdbcType=INTEGER} and sv_siid = #{svSiid,jdbcType=INTEGER}

mapper.update:
update tstaff_vote
set sv_isvoted = 1
where sv_viid = #{voteInfoId,jdbcType=INTEGER} and sv_siid = #{staffId,jdbcType=INTEGER}
